const reducer = (state,action) =>{
    if(action.type === 'CLEARCART'){
        return {
            ...state,
            cart:[]
        }
    }

    if(action.type === 'REMOVEITEM'){
        return{
            ...state,
            cart:state.cart.filter((item)=>item.id !== action.payload)
        }
    }


    if(action.type === 'INCREASEITEM'){
        return{
            ...state,
            cart:state.cart.filter((item)=>item.id !== action.payload)
        }
    }




    return state
}

export default reducer
